package br.com.logica.pares;

import java.util.Iterator;

public class Pares {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1; i<=20 ; i++ ) {
			if (i%2 == 0) 
			System.out.println(i+" é Par");
			else {
			System.out.println(i+" é Impar");
			}
		}
	}

}
